import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [{name: 'John'},{name: 'Peter'}]
    };
  }
  /*
  componentDidMount() {
    axios.get(`http://localhost:3000`)
    .then(res => {
      //const userData = res.data;
      console.log("data");
      console.log(res.data);
      this.setState({ users:res.data });
    });
  }
  */

  render() {

    let trElements;
    if (this.state.users && this.state.users.length > 0) {
      trElements = this.state.users.map((user) => 
        <tr>
          <td>{user.name}</td>
          <td><input type="image" src="edit.svg" height="50" width="50" style="background-color:yellow;" onclick="buttonClick()"/></td>
          <td><input type="image" src="trash-alt.svg" height="50" width="50" style="background-color: red"/></td>
        </tr>
        /*<
          <td></td>
          <td><button type="button" id="btnAddName" class="addnamebutton" data-toggle="modal" data-target="#myModal">Add Name</button></td>
          <td></td>
        </tr>*/
      )
    }

    return (
    <div className="App">
       <div className="left">
       <table>
        <tr>
          <th>Name</th>
          <th>Edit</th>
          <th>Delete</th>
        </tr>
        { trElements }
        </table>
      </div>    
      </div>
    );
  }
}

export default App;
